    /**
     * @dev Returns the domain separator used in the encoding of the signature for `permit`, as defined by {EIP712}.
     */
    // solhint-disable-next-line func-name-mixedcase
    function DOMAIN_SEPARATOR() external view returns (bytes32);
}","keccak256":"0x1521813d267ce22cd87d494d97495d2d7cd070e56fe7b2e05c25a8b0d586e560","license":"MIT"},"contracts/mockHog.sol":{"content":"// SPDX-License-Identifier: Apache-2.0

// taken from our contracts repo

import "./ERC20PermitWithMint.sol";

pragma solidity ^0.8.3;

contract MockHog is ERC20PermitWithMint {
    constructor(
        string memory name_,
        string memory symbol_,      
        address owner_
    ) ERC20PermitWithMint(name_, symbol_, owner_) {}

    function setBalance(address who, uint256 amount) external {
        balanceOf[who] = amount;
    }

    function setAllowance(
        address source,
        address spender,
        uint256 amount
    ) external {
        allowance[source][spender] = amount;
    }
}","keccak256":"0xc951bb688d37c3fcc466b9784217e36b08cda2ad24231b19a0b26fcd76795e4b","license":"Apache-2.0"}},"version":1}

